        /// <summary>
        /// Sets all instructions texts(Fills "Instructions" list) for all 4 pages
        /// </summary>
        private void SetAllInstructions()
        {
            string instructionText1 = "<strong>IMPORTANT:</strong>&nbsp;Read&nbsp;this&nbsp;information&nbsp;before&nbsp;selecting&nbsp;the&nbsp;<strong>Next</strong>&nbsp;button.\n\nAll&nbsp;items&nbsp;brought&nbsp;into&nbsp;the&nbsp;test&nbsp;center&nbsp;may&nbsp;be&nbsp;searched.&nbsp;Items&nbsp;susoected&nbsp;of&nbsp;being&nbsp;used&nbsp;toengage&nbsp;in&nbsp;misconduct&nbsp;may&nbsp;be\nconfiscated&nbsp;and&nbsp;retained.\n\nCell&nbsp;phones,&nbsp;smart&nbsp;watches,&nbsp;fitness&nbsp;bands,&nbsp;and&nbsp;other&nbsp;devices&nbsp;with&nbsp;recordingor&nbsp;communication&nbsp;capabilities&nbsp;are\nprohibited.\n\nYou&nbsp;may&nbsp;not&nbsp;handle&nbsp;or&nbsp;access&nbsp;such&nbsp;devices&nbsp;during&nbsp;testing&nbsp;or&nbsp;during&nbsp;breaks.&nbsp;All&nbsp;electronic&nbsp;devices&nbsp;must&nbsp;be&nbsp;powered&nbsp;off&nbsp;and\nstored&nbsp;out&nbsp;of&nbsp;sight.&nbsp;Turning&nbsp;your&nbsp;device&nbsp;to&nbsp;silent&nbsp;or&nbsp;airplane&nbsp;mode&nbsp;is&nbsp;not&nbsp;acceptable.\n\nPlease&nbsp;clear&nbsp;your&nbsp;workstation&nbsp;of&nbsp;everything&nbsp;except&nbsp;your&nbsp;testing&nbsp;computer,&nbsp;scratch&nbsp;paper,&nbsp;and&nbsp;pencil.&nbsp;Place&nbsp;all&nbsp;personal&nbsp;items\nunder&nbsp;your&nbsp;seat.&nbsp;You&nbsp;will&nbsp;not&nbsp;be&nbsp;able&nbsp;to&nbsp;access&nbsp;them&nbsp;during&nbsp;testing&nbsp;or&nbsp;during&nbsp;breaks.&nbsp;ACT&nbsp;and&nbsp;this&nbsp;test&nbsp;site&nbsp;are&nbsp;not&nbsp;responsive&nbsp;for\nthe&nbsp;loss&nbsp;of&nbsp;any&nbsp;personal&nbsp;items.&nbsp;If&nbsp;you&nbsp;brought&nbsp;a&nbsp;calculator,&nbsp;put&nbsp;it&nbsp;under&nbsp;your&nbsp;seat&nbsp;now;&nbsp;you&nbsp;may&nbsp;use&nbsp;it&nbsp;only&nbsp;during&nbsp;the\nmathematics&nbsp;test.&nbsp;Please&nbsp;keep&nbsp;the&nbsp;walking&nbsp;spaces&nbsp;clear.\n\nSelect&nbsp;the&nbsp;<strong>Next&nbsp;</strong>&nbsp;button&nbsp;to&nbsp;proceed.";


            string instructionText2 = "<strong>&nbsp;IMPORTANT:&nbsp;</strong>&nbsp;Read&nbsp;this&nbsp;information&nbsp;before&nbsp;selecting&nbsp;the&nbsp;<strong>&nbsp;Next</strong>&nbsp;button.\n\nA&nbsp;complete&nbsp;list&nbsp;of&nbsp;the&nbsp;prohibited&nbsp;behaviours&nbsp;was&nbsp;provided&nbsp;during&nbsp;the&nbsp;registration&nbsp;process.&nbsp;Please&nbsp;be&nbsp;reminded&nbsp;of&nbsp;the&nbsp;following:\n\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;You&nbsp;may&nbsp;not&nbsp;access&nbsp;an&nbsp;electronic&nbsp;device,&nbsp;other&nbsp;than&nbsp;your&nbsp;testing&nbsp;computer&nbsp;and&nbsp;calculator&nbsp;(during&nbsp;Mathematics&nbsp;only),&nbsp;at&nbsp;any\ntime&nbsp;during&nbsp;the&nbsp;testing&nbsp;or&nbsp;during&nbsp;breaks.&nbsp;All&nbsp;devices,&nbsp;other&nbsp;than&nbsp;your&nbsp;computer,&nbsp;must&nbsp;be&nbsp;powered&nbsp;off&nbsp;and&nbsp;placed&nbsp;out&nbsp;of&nbsp;the&nbsp;sight\nfrom&nbsp;the&nbsp;time&nbsp;you&nbsp;are&nbsp;admitted&nbsp;to&nbsp;the&nbsp;test&nbsp;room&nbsp;until&nbsp;you&nbsp;are&nbsp;dismissed.\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;You&nbsp;may&nbsp;not&nbsp;give&nbsp;or&nbsp;receive&nbsp;assistance&nbsp;by&nbsp;any&nbsp;means.&nbsp;This&nbsp;includes&nbsp;looking&nbsp;at&nbsp;anotherperson's&nbsp;computer&nbsp;or&nbsp;scratch&nbsp;paper.\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;You&nbsp;are&nbsp;not&nbsp;allowed&nbsp;to&nbsp;use&nbsp;any&nbsp;notes,&nbsp;dictionaries,&nbsp;or&nbsp;other&nbsp;aids.\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;You&nbsp;may&nbsp;use&nbsp;only&nbsp;the&nbsp;scratch&nbsp;paper&nbsp;provided&nbsp;to&nbsp;you.\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;You&nbsp;may&nbsp;not&nbsp;allow&nbsp;an&nbsp;alarm&nbsp;to&nbsp;sound&nbsp;in&nbsp;the&nbsp;test&nbsp;room&nbsp;or&nbsp;create&nbsp;any&nbsp;ither&nbsp;disturbance.&nbsp;If&nbsp;you&nbsp;are&nbsp;wearing&nbsp;a&nbsp;watch&nbsp;with&nbsp;an\nalarm&nbsp;or&nbsp;have&nbsp;any&nbsp;other&nbsp;device,&nbsp;you&nbsp;must&nbsp;be&nbsp;sure&nbsp;it&nbsp;is&nbsp;turned&nbsp;off.\n&nbsp;&nbsp;<strong>&nbsp;•</strong>&nbsp;&nbsp;The&nbsp;test&nbsp;is&nbsp;confidential&nbsp;and&nbsp;remains&nbsp;so&nbsp;even&nbsp;after&nbsp;the&nbsp;exam&nbsp;is&nbsp;complete.&nbsp;You&nbsp;may&nbsp;notremove&nbsp;any&nbsp;materials&nbsp;from&nbsp;the&nbsp;test\nroom.&nbsp;You&nbsp;may&nbsp;not&nbsp;discuss&nbsp;the&nbsp;test&nbsp;questions&nbsp;or&nbsp;responses&nbsp;at&nbsp;any&nbsp;time,&nbsp;including&nbsp;the&nbsp;break.\n&nbsp;&nbsp;<strong>&nbsp;•&nbsp;</strong>&nbsp;&nbsp;Finally,&nbsp;eating,&nbsp;drinking,&nbsp;and&nbsp;the&nbsp;use&nbsp;of&nbsp;tabacco&nbsp;or&nbsp;reading&nbsp;materials&nbsp;are&nbsp;not&nbsp;permitted&nbsp;in&nbsp;the&nbsp;test&nbsp;room.\n\nIf&nbsp;you&nbsp;are&nbsp;observed&nbsp;or&nbsp;suspected&nbsp;of&nbsp;engaging&nbsp;in&nbsp;prohibited&nbsp;behaviour,&nbsp;you&nbsp;will&nbsp;be&nbsp;dismissed&nbsp;and&nbsp;your&nbsp;test&nbsp;will&nbsp;not&nbsp;be&nbsp;scored.\n\n&nbsp;Testing&nbsp;staff&nbsp;will&nbsp;monitor&nbsp;the&nbsp;room.&nbsp;If&nbsp;you&nbsp;have&nbsp;a&nbsp;question&nbsp;during&nbsp;testing,&nbsp;raise&nbsp;your&nbsp;hand.\n\nSelect&nbsp;the&nbsp;<strong>&nbsp;Next&nbsp;</strong>&nbsp;button&nbsp;to&nbsp;proceed.";


            string instructionText3 = "<strong>&nbsp;IMPORTANT:</strong>&nbsp;Read&nbsp;this&nbsp;information&nbsp;before&nbsp;selecting&nbsp;the&nbsp;<strong>Next</strong>&nbsp;button.\n\nYou&nbsp;are&nbsp;about&nbsp;to&nbsp;take&nbsp;the&nbsp;ACT,&nbsp;which&nbsp;is&nbsp;composed&nbsp;of&nbsp;multiple-choice&nbsp;tests&nbsp;in&nbsp;English,&nbsp;mathematics,&nbsp;reading,&nbsp;and&nbsp;science,\nfollewed&nbsp;by&nbsp;a&nbsp;writing&nbsp;test,&nbsp;for&nbsp;which&nbsp;you&nbsp;will&nbsp;complete&nbsp;an&nbsp;essay&nbsp;written&nbsp;in&nbsp;English.\n\nThese&nbsp;tests&nbsp;measure&nbsp;skills&nbsp;and&nbsp;abilities&nbsp;highly&nbsp;related&nbsp;to&nbsp;high&nbsp;school&nbsp;course&nbsp;work&nbsp;and&nbsp;success&nbsp;in&nbsp;college.&nbsp;<strong>&nbsp;Calculators&nbsp;may&nbsp;be\nused&nbsp;on&nbsp;the&nbsp;mathematics&nbsp;only&nbsp;</strong>&nbsp;.\n\nFor&nbsp;each&nbsp;multiple-choice&nbsp;question,&nbsp;choose&nbsp;the&nbsp;best&nbsp;answer&nbsp;by&nbsp;selecting&nbsp;the&nbsp;circle&nbsp;next&nbsp;to&nbsp;it.&nbsp;If&nbsp;you&nbsp;change&nbsp;your&nbsp;mind&nbsp;about&nbsp;an\nanswer,&nbsp;choose&nbsp;a&nbsp;different&nbsp;answer&nbsp;and&nbsp;select&nbsp;the&nbsp;circle&nbsp;next&nbsp;to&nbsp;it.&nbsp;Select&nbsp;the&nbsp;<strong>&nbsp;Next&nbsp;</strong>&nbsp;button&nbsp;to&nbsp;move&nbsp;to&nbsp;the&nbsp;next&nbsp;question.&nbsp;For&nbsp;the\nwriting&nbsp;test,&nbsp;type&nbsp;your&nbsp;essay&nbsp;into&nbsp;the&nbsp;text&nbsp;box&nbsp;below&nbsp;the&nbsp;writing&nbsp;prompt&nbsp;and&nbsp;select&nbsp;the&nbsp;<strong>Next&nbsp;</strong>&nbsp;button&nbsp;when&nbsp;finished.\n\nYour&nbsp;score&nbsp;on&nbsp;each&nbsp;multiple-choice&nbsp;test&nbsp;will&nbsp;be&nbsp;based&nbsp;only&nbsp;on&nbsp;the&nbsp;number&nbsp;of&nbsp;question&nbsp;you&nbsp;answered&nbsp;correctly&nbsp;during&nbsp;the&nbsp;time\nallowed&nbsp;for&nbsp;that&nbsp;test.&nbsp;You&nbsp;will&nbsp;<strong>&nbsp;not&nbsp;</strong>&nbsp;be&nbsp;penalized&nbsp;for&nbsp;guessing.&nbsp;<strong>&nbsp;It&nbsp;is&nbsp;to&nbsp;your&nbsp;advantage&nbsp;to&nbsp;answer&nbsp;every&nbsp;question</strong>.&nbsp;Your&nbsp;score&nbsp;on\nthe&nbsp;writing&nbsp;test&nbsp;is&nbsp;based&nbsp;on&nbsp;the&nbsp;essay&nbsp;you&nbsp;type&nbsp;into&nbsp;the&nbsp;text&nbsp;box.\n\nYour&nbsp;computer&nbsp;will&nbsp;keep&nbsp;the&nbsp;official&nbsp;time&nbsp;for&nbsp;your&nbsp;examination.&nbsp;There&nbsp;will&nbsp;be&nbsp;a&nbsp;countdown&nbsp;timer&nbsp;that&nbsp;will&nbsp;tell&nbsp;you&nbsp;the&nbsp;time\nremaining&nbsp;for&nbsp;the&nbsp;test&nbsp;you&nbsp;are&nbsp;working&nbsp;on.&nbsp;When&nbsp;5&nbsp;minutes&nbsp;remain&nbsp;on&nbsp;each&nbsp;test,&nbsp;a&nbsp;message&nbsp;will&nbsp;appear&nbsp;on&nbsp;your&nbsp;screen&nbsp;to&nbsp;serve&nbsp;as\na&nbsp;warning&nbsp;before&nbsp;time&nbsp;is&nbsp;up.&nbsp;When&nbsp;time&nbsp;runs&nbsp;out,&nbsp;submit&nbsp;your&nbsp;test&nbsp;according&nbsp;to&nbsp;the&nbsp;instructions&nbsp;on&nbsp;your&nbsp;screen.\n\n&nbsp;If&nbsp;you&nbsp;finish&nbsp;a&nbsp;test&nbsp;easily,&nbsp;you&nbsp;may&nbsp;sit&nbsp;quietly&nbsp;or&nbsp;use&nbsp;the&nbsp;remaining&nbsp;time&nbsp;to&nbsp;reconsider&nbsp;questions&nbsp;you&nbsp;are&nbsp;uncertain&nbsp;about&nbsp;in&nbsp;that&nbsp;test\nor&nbsp;to&nbsp;review&nbsp;your&nbsp;essay.&nbsp;You&nbsp;will&nbsp;<strong>&nbsp;not&nbsp;</strong>&nbsp;be&nbsp;able&nbsp;to&nbsp;look&nbsp;back&nbsp;at&nbsp;the&nbsp;test&nbsp;questions&nbsp;or&nbsp;the&nbsp;essay&nbsp;once&nbsp;the&nbsp;test&nbsp;is&nbsp;submitted.\n\nPlease&nbsp;read&nbsp;and&nbsp;agree&nbsp;to&nbsp;the&nbsp;Examinee&nbsp;Statement&nbsp;on&nbsp;the&nbsp;next&nbsp;screen.&nbsp;The&nbsp;Examinee&nbsp;Statement&nbsp;section&nbsp;is&nbsp;not&nbsp;scored.\n\nSelect&nbsp;the&nbsp;<strong>&nbsp;Next&nbsp;</strong>&nbsp;button&nbsp;to&nbsp;proceed.";


            string instructionText4 = "By&nbsp;accessing&nbsp;the&nbsp;online&nbsp;test,&nbsp;I&nbsp;agree&nbsp;to&nbsp;comply&nbsp;with&nbsp;and&nbsp;be&nbsp;bound&nbsp;by&nbsp;the&nbsp;<i>&nbsp;Terms&nbsp;and&nbsp;Conditions:&nbsp;Testing&nbsp;Rules&nbsp;and&nbsp;Policies&nbsp;for&nbsp;the\n&nbsp;ACT&nbsp;Test</i>&nbsp;provided&nbsp;in&nbsp;the&nbsp;ACT&nbsp;registration&nbsp;material&nbsp;for&nbsp;this&nbsp;test,&nbsp;including&nbsp;those&nbsp;conserning&nbsp;test&nbsp;security,&nbsp;score&nbsp;cancellation,\nexaminee&nbsp;remedies,&nbsp;binding&nbsp;arbitration,&nbsp;and&nbsp;consent&nbsp;to&nbsp;the&nbsp;processing&nbsp;of&nbsp;my&nbsp;personally&nbsp;identifying&nbsp;information,&nbsp;including&nbsp;the\ncollection,&nbsp;use,&nbsp;transfer,&nbsp;and&nbsp;disclosure&nbsp;of&nbsp;information&nbsp;as&nbsp;described&nbsp;in&nbsp;the&nbsp;ACT&nbsp;Privace&nbsp;Policy&nbsp;(www.act.org/privacy.html).\n\nI&nbsp;understand&nbsp;that&nbsp;ACT&nbsp;owns&nbsp;the&nbsp;test&nbsp;questions&nbsp;and&nbsp;responces&nbsp;and&nbsp;affirm&nbsp;that&nbsp;I&nbsp;will&nbsp;not&nbsp;share&nbsp;any&nbsp;test&nbsp;questions&nbsp;or&nbsp;responces&nbsp;with\nanyone&nbsp;by&nbsp;any&nbsp;form&nbsp;of&nbsp;communication&nbsp;before,&nbsp;during,&nbsp;or&nbsp;after&nbsp;the&nbsp;test&nbsp;administration.&nbsp;I&nbsp;understand&nbsp;that&nbsp;assuming&nbsp;anyone&nbsp;else's\n&nbsp;identity&nbsp;to&nbsp;take&nbsp;this&nbsp;test&nbsp;is&nbsp;strictly&nbsp;prohibited&nbsp;and&nbsp;may&nbsp;violate&nbsp;the&nbsp;law&nbsp;and&nbsp;subject&nbsp;me&nbsp;to&nbsp;legal&nbsp;penalties.\n\n&nbsp;<strong>International&nbsp;Examinees:&nbsp;</strong>&nbsp;By&nbsp;accessing&nbsp;the&nbsp;online&nbsp;test,&nbsp;I&nbsp;an&nbsp;also&nbsp;providing&nbsp;my&nbsp;consent&nbsp;to&nbsp;ACT&nbsp;to&nbsp;transfer&nbsp;my&nbsp;personally\nidentifying&nbsp;information&nbsp;to&nbsp;the&nbsp;United&nbsp;States&nbsp;to&nbsp;ACT,&nbsp;or&nbsp;a&nbsp;third-party&nbsp;service&nbsp;provider,&nbsp;where&nbsp;it&nbsp;will&nbsp;be&nbsp;subject&nbsp;to&nbsp;use&nbsp;and&nbsp;disclosure\nunser&nbsp;the&nbsp;laws&nbsp;of&nbsp;the&nbsp;United&nbsp;States.&nbsp;I&nbsp;acknowledge&nbsp;and&nbsp;agree&nbsp;that&nbsp;it&nbsp;may&nbsp;also&nbsp;be&nbsp;accessible&nbsp;to&nbsp;law&nbsp;enforcement&nbsp;and&nbsp;national\nsecurity&nbsp;authorities&nbsp;im&nbsp;the&nbsp;United&nbsp;States.\n\nBy&nbsp;selecting&nbsp;<strong>&nbsp;Accept</strong>&nbsp;below&nbsp;and/or&nbsp;accessing&nbsp;the&nbsp;online&nbsp;test,&nbsp;I&nbsp;confirm&nbsp;my&nbsp;acceptance&nbsp;of&nbsp;the&nbsp;above&nbsp;terms.";


            Instructions.Add(new Instruction { InstructionText = instructionText1, Header = "General Instructions" });
            Instructions.Add(new Instruction { InstructionText = instructionText2, Header = "Prohibited Behaviour" });
            Instructions.Add(new Instruction { InstructionText = instructionText3, Header = "Test Directions" });
            Instructions.Add(new Instruction { InstructionText = instructionText4, Header = "Examinee Statement" });
        }



        protected virtual void OnPropertyChanged(string propertyName = null)
        {
            PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));
        }
    }

}
